import React, { useState, useMemo } from 'react';
import CollectionCard from './components/CollectionCard';
import DashboardStats from './components/DashboardStats';
import LoadingSpinner from './components/LoadingSpinner';
import TrendingSection from './components/TrendingSection';
import PortfolioSection from './components/PortfolioSection';
import AdvancedFilters from './components/AdvancedFilters';
import { useNFTData } from './hooks/useNFTData';
import './styles/App.css';

function App() {
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState('name');
  const [viewMode, setViewMode] = useState('grid');
  const [activeTab, setActiveTab] = useState('collections');
  const [filters, setFilters] = useState({});

  const { 
    collections, 
    trendingCollections, 
    userPortfolio, 
    loading, 
    error, 
    ethPrice, 
    lastUpdated, 
    refreshData,
    setUserAddress 
  } = useNFTData();

  const dashboardStats = useMemo(() => {
    if (collections.length === 0) return { totalCollections: 0, totalVolume: 0, avgFloorPrice: 0, totalOwners: 0 };

    return {
      totalCollections: collections.length,
      totalVolume: collections.reduce((sum, col) => sum + (col.volume24h || 0), 0),
      avgFloorPrice: collections.reduce((sum, col) => sum + (col.floorPrice || 0), 0) / collections.length,
      totalOwners: collections.reduce((sum, col) => sum + (col.owners || 0), 0)
    };
  }, [collections]);

  const filteredAndSortedCollections = useMemo(() => {
    let filtered = collections.filter(collection => {
      // Text search
      const matchesSearch = collection.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                           collection.symbol.toLowerCase().includes(searchTerm.toLowerCase());

      // Price filter
      const matchesPrice = (!filters.minPrice || collection.floorPrice >= parseFloat(filters.minPrice)) &&
                          (!filters.maxPrice || collection.floorPrice <= parseFloat(filters.maxPrice));

      // Volume filter
      const matchesVolume = !filters.minVolume || collection.volume24h >= parseFloat(filters.minVolume);

      // Size filter
      let matchesSize = true;
      if (filters.sizeFilter === 'small') matchesSize = collection.totalSupply < 1000;
      else if (filters.sizeFilter === 'medium') matchesSize = collection.totalSupply >= 1000 && collection.totalSupply <= 10000;
      else if (filters.sizeFilter === 'large') matchesSize = collection.totalSupply > 10000;

      // Verified filter
      const matchesVerified = !filters.verifiedOnly || collection.verified;

      return matchesSearch && matchesPrice && matchesVolume && matchesSize && matchesVerified;
    });

    return filtered.sort((a, b) => {
      switch (sortBy) {
        case 'floorPrice':
          return (b.floorPrice || 0) - (a.floorPrice || 0);
        case 'volume24h':
          return (b.volume24h || 0) - (a.volume24h || 0);
        case 'change24h':
          return (b.change24h || 0) - (a.change24h || 0);
        case 'owners':
          return (b.owners || 0) - (a.owners || 0);
        case 'marketCap':
          return (b.marketCap || 0) - (a.marketCap || 0);
        case 'sales24h':
          return (b.sales24h || 0) - (a.sales24h || 0);
        default:
          return a.name.localeCompare(b.name);
      }
    });
  }, [collections, searchTerm, sortBy, filters]);

  const handleFilterChange = (key, value) => {
    setFilters(prev => ({ ...prev, [key]: value }));
  };

  const handleTabChange = (tab) => {
    setActiveTab(tab);
  };

  if (loading) return <LoadingSpinner />;
  if (error) return <div className="error-message">Error: {error}</div>;

  return (
    <div className="app">
      <header className="header">
        <h1>NFT Analytics Dashboard</h1>
        <p>Real-time NFT analytics powered by Magic Eden â€¢ ETH: ${ethPrice.toFixed(2)}</p>
        {lastUpdated && (
          <div className="last-updated">
            Last updated: {lastUpdated.toLocaleTimeString()}
            <button onClick={refreshData} className="refresh-btn">ðŸ”„</button>
          </div>
        )}
      </header>

      <DashboardStats stats={dashboardStats} />

      <div className="tab-navigation">
        <button 
          className={activeTab === 'collections' ? 'tab active' : 'tab'}
          onClick={() => handleTabChange('collections')}
        >
          ðŸ“Š Collections
        </button>
        <button 
          className={activeTab === 'trending' ? 'tab active' : 'tab'}
          onClick={() => handleTabChange('trending')}
        >
          ðŸ”¥ Trending
        </button>
        <button 
          className={activeTab === 'portfolio' ? 'tab active' : 'tab'}
          onClick={() => handleTabChange('portfolio')}
        >
          ðŸ’¼ Portfolio
        </button>
      </div>

      {activeTab === 'collections' && (
        <>
          <div className="search-bar">
            <input
              type="text"
              placeholder="Search collections..."
              className="search-input"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>

          <AdvancedFilters
            filters={filters}
            onFilterChange={handleFilterChange}
            onSortChange={setSortBy}
            onViewModeChange={setViewMode}
            viewMode={viewMode}
          />
        </>
      )}

      {activeTab === 'collections' && (
        <div className="collections-section">
          {filteredAndSortedCollections.length > 0 ? (
            <div className={`collections-${viewMode}`}>
              {filteredAndSortedCollections.map(collection => (
                <CollectionCard 
                  key={collection.id} 
                  collection={collection}
                  viewMode={viewMode}
                />
              ))}
            </div>
          ) : (
            <div className="no-results">
              No collections found matching your criteria.
            </div>
          )}
        </div>
      )}

      {activeTab === 'trending' && (
        <TrendingSection 
          collections={trendingCollections} 
          loading={loading}
        />
      )}

      {activeTab === 'portfolio' && (
        <PortfolioSection 
          portfolio={userPortfolio}
          onAddressChange={setUserAddress}
          loading={loading}
        />
      )}
    </div>
  );
}

export default App;
