import { useState, useEffect, useCallback } from 'react';
import { monadAPI, monadNetworkAPI } from '../services/apiService';

export const useNFTData = () => {
  const [collections, setCollections] = useState([]);
  const [trendingCollections, setTrendingCollections] = useState([]);
  const [userPortfolio, setUserPortfolio] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchLoading, setSearchLoading] = useState(false);
  const [error, setError] = useState(null);
  const [monPrice, setMonPrice] = useState(0);
  const [networkStats, setNetworkStats] = useState(null);
  const [selectedNetwork, setSelectedNetwork] = useState('mainnet');
  const [userAddress, setUserAddress] = useState('');
  const [lastUpdated, setLastUpdated] = useState(null);

  // Pagination states
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [hasNextPage, setHasNextPage] = useState(false);
  const [hasPrevPage, setHasPrevPage] = useState(false);
  const [continuation, setContinuation] = useState(null);

  // Search states
  const [searchQuery, setSearchQuery] = useState('');
  const [isSearchMode, setIsSearchMode] = useState(false);

  const ITEMS_PER_PAGE = 20;

  const fetchCollections = useCallback(async (page = 1) => {
    setLoading(true);
    setError(null);

    try {
      const offset = (page - 1) * ITEMS_PER_PAGE;
      const collectionsData = await monadAPI.getCollections(ITEMS_PER_PAGE, offset, selectedNetwork);

      if (collectionsData && collectionsData.collections) {
        setCollections(collectionsData.collections);
        setHasNextPage(!!collectionsData.continuation);
        setHasPrevPage(page > 1);
        setContinuation(collectionsData.continuation);
        setCurrentPage(page);
        setTotalPages(page + (collectionsData.continuation ? 10 : 0));
      } else {
        setCollections([]);
        setError('No Monad collections found');
      }

      setLastUpdated(new Date());
    } catch (err) {
      console.error('Error fetching Monad collections:', err);
      setError('Failed to fetch Monad NFT data');
      setCollections([]);
    } finally {
      setLoading(false);
    }
  }, [ITEMS_PER_PAGE, selectedNetwork]);

  const fetchTrendingCollections = useCallback(async (page = 1) => {
    try {
      const offset = (page - 1) * ITEMS_PER_PAGE;
      const trendingData = await monadAPI.getTrendingCollections('24h', ITEMS_PER_PAGE, offset, selectedNetwork);

      if (trendingData && trendingData.collections) {
        setTrendingCollections(trendingData.collections);
      }
    } catch (err) {
      console.error('Error fetching trending Monad collections:', err);
    }
  }, [ITEMS_PER_PAGE, selectedNetwork]);

  const searchCollections = useCallback(async (query, page = 1) => {
    if (!query.trim()) {
      setIsSearchMode(false);
      fetchCollections(1);
      return;
    }

    setSearchLoading(true);
    setIsSearchMode(true);
    setError(null);

    try {
      const offset = (page - 1) * ITEMS_PER_PAGE;
      const searchData = await monadAPI.searchCollections(query, ITEMS_PER_PAGE, offset, selectedNetwork);

      if (searchData && searchData.collections) {
        setCollections(searchData.collections);
        setHasNextPage(!!searchData.continuation);
        setHasPrevPage(page > 1);
        setContinuation(searchData.continuation);
        setCurrentPage(page);
        setTotalPages(page + (searchData.continuation ? 10 : 0));
      } else {
        setCollections([]);
        setError('No Monad collections found for your search');
      }
    } catch (err) {
      console.error('Error searching Monad collections:', err);
      setError('Monad search failed');
      setCollections([]);
    } finally {
      setSearchLoading(false);
    }
  }, [ITEMS_PER_PAGE, fetchCollections, selectedNetwork]);

  const fetchMonadData = useCallback(async () => {
    try {
      const [price, stats] = await Promise.all([
        monadAPI.getMonPrice(),
        monadNetworkAPI.getNetworkStats(selectedNetwork)
      ]);
      setMonPrice(price);
      setNetworkStats(stats);
    } catch (err) {
      console.error('Error fetching Monad data:', err);
    }
  }, [selectedNetwork]);

  const fetchUserPortfolio = useCallback(async (address) => {
    if (!address) return;

    try {
      const portfolio = await monadAPI.getUserCollections(address, 0, 20, selectedNetwork);
      setUserPortfolio(portfolio);
    } catch (err) {
      console.error('Error fetching Monad portfolio:', err);
    }
  }, [selectedNetwork]);

  useEffect(() => {
    fetchCollections(1);
    fetchTrendingCollections(1);
    fetchMonadData();
  }, [fetchCollections, fetchTrendingCollections, fetchMonadData]);

  useEffect(() => {
    // Refresh data when network changes
    if (isSearchMode && searchQuery) {
      searchCollections(searchQuery, 1);
    } else {
      fetchCollections(1);
    }
    fetchTrendingCollections(1);
    if (userAddress) {
      fetchUserPortfolio(userAddress);
    }
  }, [selectedNetwork]);

  useEffect(() => {
    if (userAddress) {
      fetchUserPortfolio(userAddress);
    }
  }, [userAddress, fetchUserPortfolio]);

  const refreshData = useCallback(() => {
    if (isSearchMode && searchQuery) {
      searchCollections(searchQuery, 1);
    } else {
      fetchCollections(1);
    }
    fetchTrendingCollections(1);
    fetchMonadData();
    if (userAddress) {
      fetchUserPortfolio(userAddress);
    }
  }, [fetchCollections, fetchTrendingCollections, fetchMonadData, fetchUserPortfolio, userAddress, isSearchMode, searchQuery, searchCollections]);

  const handlePageChange = useCallback((page) => {
    if (isSearchMode && searchQuery) {
      searchCollections(searchQuery, page);
    } else {
      fetchCollections(page);
    }
  }, [isSearchMode, searchQuery, searchCollections, fetchCollections]);

  const handleSearch = useCallback((query) => {
    setSearchQuery(query);
    searchCollections(query, 1);
  }, [searchCollections]);

  const clearSearch = useCallback(() => {
    setSearchQuery('');
    setIsSearchMode(false);
    fetchCollections(1);
  }, [fetchCollections]);

  return {
    collections,
    trendingCollections,
    userPortfolio,
    loading,
    searchLoading,
    error,
    monPrice,
    networkStats,
    selectedNetwork,
    lastUpdated,
    currentPage,
    totalPages,
    hasNextPage,
    hasPrevPage,
    searchQuery,
    isSearchMode,
    refreshData,
    setUserAddress,
    setSelectedNetwork,
    handlePageChange,
    handleSearch,
    clearSearch,
    fetchTrendingCollections
  };
};
