import React, { useState } from 'react';

const PortfolioSection = ({ portfolio, onAddressChange, loading }) => {
  const [address, setAddress] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (address.trim()) {
      onAddressChange(address.trim());
    }
  };

  const formatPrice = (price) => {
    if (!price || price === 0) return '0 MON';
    return `${parseFloat(price).toLocaleString()} MON`;
  };

  const totalValue = portfolio.reduce((sum, item) => sum + (item.totalValue || 0), 0);

  return (
    <div className="portfolio-section">
      <h2>ðŸ’¼ Monad Portfolio Tracker</h2>
      <p className="portfolio-note">Showing only Monad-related collections from your portfolio</p>

      <form onSubmit={handleSubmit} className="address-form">
        <input
          type="text"
          placeholder="Enter Monad wallet address (0x...)"
          value={address}
          onChange={(e) => setAddress(e.target.value)}
          className="address-input"
        />
        <button type="submit" className="track-button">Track Portfolio</button>
      </form>

      {loading && <div className="portfolio-loading">Loading portfolio...</div>}

      {portfolio.length > 0 && (
        <div className="portfolio-content">
          <div className="portfolio-summary">
            <div className="summary-card">
              <h3>Total Portfolio Value (MON)</h3>
              <div className="total-value">{formatPrice(totalValue)}</div>
            </div>
            <div className="summary-card">
              <h3>Collections Owned</h3>
              <div className="total-collections">{portfolio.length}</div>
            </div>
            <div className="summary-card">
              <h3>Total NFTs</h3>
              <div className="total-nfts">
                {portfolio.reduce((sum, item) => sum + (item.ownedCount || 0), 0)}
              </div>
            </div>
          </div>

          <div className="portfolio-collections">
            {portfolio.map(item => (
              <div key={item.id} className="portfolio-item">
                <img 
                  src={item.image} 
                  alt={item.name}
                  className="portfolio-image"
                  onError={(e) => {
                    e.target.src = 'https://via.placeholder.com/80x80/6366f1/ffffff?text=NFT';
                  }}
                />
                <div className="portfolio-details">
                  <h4>{item.name}</h4>
                  <div className="portfolio-stats">
                    <span>Owned: {item.ownedCount}</span>
                    <span>Floor: {formatPrice(item.floorPrice)}</span>
                    <span>Value: {formatPrice(item.totalValue)}</span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default PortfolioSection;
