import React, { useState, useMemo } from 'react';
import CollectionCard from './components/CollectionCard';
import DashboardStats from './components/DashboardStats';
import LoadingSpinner from './components/LoadingSpinner';
import ActivityFeed from './components/ActivityFeed';
import { useNFTData } from './hooks/useNFTData';
import './styles/App.css';

function App() {
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState('name');
  const [selectedCollection, setSelectedCollection] = useState(null);
  const [alerts, setAlerts] = useState([]);
  const [viewMode, setViewMode] = useState('grid');

  const { collections, loading, error, monPrice, networkStats, lastUpdated, refreshData } = useNFTData();

  const dashboardStats = useMemo(() => {
    if (collections.length === 0) return { totalCollections: 0, totalVolume: 0, avgFloorPrice: 0, totalOwners: 0 };

    return {
      totalCollections: collections.length,
      totalVolume: collections.reduce((sum, col) => sum + (col.volume24h || 0), 0),
      avgFloorPrice: collections.reduce((sum, col) => sum + (col.floorPrice || 0), 0) / collections.length,
      totalOwners: collections.reduce((sum, col) => sum + (col.owners || 0), 0)
    };
  }, [collections]);

  const filteredAndSortedCollections = useMemo(() => {
    let filtered = collections.filter(collection =>
      collection.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      collection.symbol.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return filtered.sort((a, b) => {
      switch (sortBy) {
        case 'floorPrice':
          return (b.floorPrice || 0) - (a.floorPrice || 0);
        case 'volume':
          return (b.volume24h || 0) - (a.volume24h || 0);
        case 'change':
          return (b.change24h || 0) - (a.change24h || 0);
        case 'owners':
          return (b.owners || 0) - (a.owners || 0);
        default:
          return a.name.localeCompare(b.name);
      }
    });
  }, [collections, searchTerm, sortBy]);

  const handleSetAlert = (alertData) => {
    setAlerts(prev => [...prev, { ...alertData, id: Date.now() }]);
  };

  if (loading) return <LoadingSpinner />;
  if (error) return <div className="error-message">Error: {error}</div>;

  return (
    <div className="app">
      <header className="header">
        <h1>Monad NFT Dashboard</h1>
        <p>Discover and track NFT collections on Monad â€¢ MON: ${monPrice.toFixed(2)}</p>
        {networkStats && (
          <div className="network-stats">
            Block: {networkStats.blockHeight.toLocaleString()} â€¢ TPS: {networkStats.tps.toLocaleString()} â€¢ Gas: {networkStats.gasPrice} MON
          </div>
        )}
        {lastUpdated && (
          <div className="last-updated">
            Last updated: {lastUpdated.toLocaleTimeString()}
            <button onClick={refreshData} className="refresh-btn">ðŸ”„</button>
          </div>
        )}
      </header>

      <DashboardStats stats={dashboardStats} />

      <div className="controls">
        <div className="search-filter">
          <input
            type="text"
            placeholder="Search collections..."
            className="search-input"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <select
            className="filter-select"
            value={sortBy}
            onChange={(e) => setSortBy(e.target.value)}
          >
            <option value="name">Sort by Name</option>
            <option value="floorPrice">Sort by Floor Price</option>
            <option value="volume">Sort by Volume</option>
            <option value="change">Sort by 24h Change</option>
            <option value="owners">Sort by Owners</option>
          </select>
          <div className="view-toggle">
            <button 
              className={viewMode === 'grid' ? 'active' : ''}
              onClick={() => setViewMode('grid')}
            >
              Grid
            </button>
            <button 
              className={viewMode === 'list' ? 'active' : ''}
              onClick={() => setViewMode('list')}
            >
              List
            </button>
          </div>
        </div>
      </div>

      <div className="main-content">
        <div className="collections-section">
          {filteredAndSortedCollections.length > 0 ? (
            <div className={`collections-${viewMode}`}>
              {filteredAndSortedCollections.map(collection => (
                <CollectionCard 
                  key={collection.id} 
                  collection={collection}
                  onSetAlert={handleSetAlert}
                  onClick={() => setSelectedCollection(collection)}
                  viewMode={viewMode}
                />
              ))}
            </div>
          ) : (
            <div className="no-results">
              No collections found matching your search.
            </div>
          )}
        </div>

        {selectedCollection && (
          <div className="sidebar">
            <div className="sidebar-header">
              <h3>{selectedCollection.name}</h3>
              <button onClick={() => setSelectedCollection(null)}>âœ•</button>
            </div>
            <ActivityFeed collectionId={selectedCollection.id} />
          </div>
        )}
      </div>

      {alerts.length > 0 && (
        <div className="alerts-panel">
          <h3>Active Alerts ({alerts.length})</h3>
          {alerts.slice(0, 3).map(alert => (
            <div key={alert.id} className="alert-item">
              {alert.collectionName}: {alert.type} {alert.targetPrice} ETH
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default App;
