import React, { useState, useMemo } from 'react';
import CollectionCard from './components/CollectionCard';
import DashboardStats from './components/DashboardStats';
import { nftCollections, dashboardStats } from './data/mockData';
import './styles/App.css';

function App() {
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState('name');

  const filteredAndSortedCollections = useMemo(() => {
    let filtered = nftCollections.filter(collection =>
      collection.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      collection.symbol.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return filtered.sort((a, b) => {
      switch (sortBy) {
        case 'floorPrice':
          return b.floorPrice - a.floorPrice;
        case 'volume':
          return b.volume24h - a.volume24h;
        case 'change':
          return b.change24h - a.change24h;
        case 'owners':
          return b.owners - a.owners;
        default:
          return a.name.localeCompare(b.name);
      }
    });
  }, [searchTerm, sortBy]);

  return (
    <div className="app">
      <header className="header">
        <h1>Monad NFT Dashboard</h1>
        <p>Discover and track NFT collections on the fastest blockchain - Monad</p>
      </header>

      <DashboardStats stats={dashboardStats} />

      <div className="search-filter">
        <input
          type="text"
          placeholder="Search collections..."
          className="search-input"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <select
          className="filter-select"
          value={sortBy}
          onChange={(e) => setSortBy(e.target.value)}
        >
          <option value="name">Sort by Name</option>
          <option value="floorPrice">Sort by Floor Price</option>
          <option value="volume">Sort by Volume</option>
          <option value="change">Sort by 24h Change</option>
          <option value="owners">Sort by Owners</option>
        </select>
      </div>

      {filteredAndSortedCollections.length > 0 ? (
        <div className="collections-grid">
          {filteredAndSortedCollections.map(collection => (
            <CollectionCard key={collection.id} collection={collection} />
          ))}
        </div>
      ) : (
        <div className="no-results">
          No collections found matching your search.
        </div>
      )}
    </div>
  );
}

export default App;
